INSERT INTO `comment` VALUES ('598031d1686d4cb59eadbbe8ffd984a0', '123123', '123123@qq.com', '<pre>/**<br> * Copyright (c) 2015, 玛雅牛［李飞］ (lifei<a href=\"javascript:;\" class=\"qing-comment-aite\">@wellbole.com).</a><br> *<br> * Licensed under the Apache License, Version 2.0 (the &quot;License&quot;);<br> * you may not use this file except in compliance with the License.<br> * You may obtain a copy of the License at<br> *<br> *      http://www.apache.org/licenses/LICENSE-2.0<br> *<br> * Unless required by applicable law or agreed to in writing, software<br> * distributed under the License is distributed on an &quot;AS IS&quot; BASIS,<br> * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.<br> * See the License for the specific language governing permissions and<br> * limitations under the License.<br> */<br>package com.jfinal.plugin.scheduler;<br><br>import java.util.HashMap;<br>import java.util.Map;<br>import java.util.Set;<br>import java.util.concurrent.ScheduledFuture;<br>import java.util.concurrent.ScheduledThreadPoolExecutor;<br>import java.util.concurrent.TimeUnit;<br><br>import com.jfinal.kit.Prop;<br>import com.jfinal.kit.PropKit;<br>import com.jfinal.kit.StrKit;<br>import com.jfinal.log.Log;<br>import com.jfinal.plugin.IPlugin;<br><br>import it.sauronsoftware.cron4j.Scheduler;<br><br>/**<br> * <a href=\"javascript:;\" class=\"qing-comment-aite\">@ClassName:</a> SchedulerPlugin<br> * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 简单任务调度插件，整合了cron4j和ScheduledThreadPoolExecutor<br> * <a href=\"javascript:;\" class=\"qing-comment-aite\">@author</a> 李飞 (lifei<a href=\"javascript:;\" class=\"qing-comment-aite\">@wellbole.com)</a><br> * <a href=\"javascript:;\" class=\"qing-comment-aite\">@date</a> 2015年8月1日 下午10:01:37<br> * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br> */<br>public class SchedulerPlugin implements IPlugin {<br><br>	private static Log LOG = Log.getLog(&quot;SchedulerPlugin&quot;);<br><br>	/**<br>	 * cron调度器<br>	 */<br>	private final Scheduler cronScheduler = new Scheduler();<br><br>	/**<br>	 * ScheduledThreadPoolExecutor调度器<br>	 */<br>	private final ScheduledThreadPoolExecutor taskScheduler;<br><br>	/**<br>	 * 调度任务配置文件<br>	 */<br>	private final String jobConfigFile;<br><br>	/**<br>	 * &lt;p&gt;<br>	 * Title: SchedulerPlugin<br>	 * &lt;/p&gt;<br>	 * &lt;p&gt;<br>	 * Description: 构造函数(线程池依据系统核心数自动设定)<br>	 * &lt;/p&gt;<br>	 * <br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	public SchedulerPlugin() {<br>		this(getBestPoolSize(), null);<br>	}<br><br>	/**<br>	 * &lt;p&gt;<br>	 * Title: SchedulerPlugin<br>	 * &lt;/p&gt;<br>	 * &lt;p&gt;<br>	 * Description: 构造函数(指定调度线程池大小)<br>	 * &lt;/p&gt;<br>	 * <br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> scheduledThreadPoolSize<br>	 *            调度线程池大小<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.3.0<br>	 */<br>	public SchedulerPlugin(int scheduledThreadPoolSize) {<br>		this(scheduledThreadPoolSize, null);<br>	}<br><br>	/**<br>	 * &lt;p&gt;<br>	 * Title: SchedulerPlugin<br>	 * &lt;/p&gt;<br>	 * &lt;p&gt;<br>	 * Description: 构造函数(指定调度任务配置文件，线程池依据系统核心数自动设定)<br>	 * &lt;/p&gt;<br>	 * <br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> jobConfigFile<br>	 *            调度任务配置文件<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	public SchedulerPlugin(String jobConfigFile) {<br>		this(getBestPoolSize(), jobConfigFile);<br>	}<br><br>	/**<br>	 * &lt;p&gt;<br>	 * Title: SchedulerPlugin<br>	 * &lt;/p&gt;<br>	 * &lt;p&gt;<br>	 * Description: 构造函数(指定调度线程池大小和调度任务配置文件)<br>	 * &lt;/p&gt;<br>	 * <br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> scheduledThreadPoolSize<br>	 *            调度线程池大小<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> jobConfigFile<br>	 *            调度任务配置文件<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.3.0<br>	 */<br>	public SchedulerPlugin(int scheduledThreadPoolSize, String jobConfigFile) {<br>		this.jobConfigFile = jobConfigFile;<br>		this.taskScheduler = new ScheduledThreadPoolExecutor(scheduledThreadPoolSize);<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> cronSchedule<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 添加基于Linux下的crontab表达式的调度任务(Runnable)<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> task<br>	 *            定期执行的任务(Runnable)<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> cronExpression<br>	 *            cron调度表达式<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	public void cronSchedule(Runnable task, String cronExpression) {<br>		this.cronScheduler.schedule(cronExpression, task);<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> fixedRateSchedule<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 立即启动，并以固定的频率来运行任务。后续任务的启动时间不受前次任务延时影响（并行）。<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> task<br>	 *            定期执行的任务<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> periodSeconds<br>	 *            每次执行任务的间隔时间(单位秒)<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@return</a><br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	public ScheduledFuture&lt;?&gt; fixedRateSchedule(Runnable task, int periodSeconds) {<br>		return taskScheduler.scheduleAtFixedRate(task, 0, periodSeconds, TimeUnit.SECONDS);<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> fixedDelaySchedule<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 立即启动，两次任务间保持固定的时间间隔(任务串行执行，前一个结束之后间隔固定时间后一个才会启动)<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> task<br>	 *            定期执行的任务<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> periodSeconds<br>	 *            每次执行任务的间隔时间(单位秒)<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@return</a><br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	public ScheduledFuture&lt;?&gt; fixedDelaySchedule(Runnable task, int periodSeconds) {<br>		return taskScheduler.scheduleWithFixedDelay(task, 0, periodSeconds, TimeUnit.SECONDS);<br>	}<br><br>	<a href=\"javascript:;\" class=\"qing-comment-aite\">@Override</a><br>	public boolean start() {<br>		if (this.jobConfigFile != null) {<br>			// 任务配置文件非空,从配置文件汇总加载任务<br>			loadJobsFromConfigFile();<br>		}<br>		this.cronScheduler.setDaemon(true);<br>		this.cronScheduler.start();<br>		LOG.info(&quot;SchedulerPlugin is started&quot;);<br>		return true;<br>	}<br><br>	<a href=\"javascript:;\" class=\"qing-comment-aite\">@Override</a><br>	public boolean stop() {<br>		this.cronScheduler.stop();<br>		this.taskScheduler.shutdown();<br>		LOG.info(&quot;SchedulerPlugin is stopped&quot;);<br>		return true;<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> loadJobsFromConfigFile<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 从配置文件汇总加载任务<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	private void loadJobsFromConfigFile() {<br>		// 获取job配置文件<br>		Prop jobProp = PropKit.use(this.jobConfigFile);<br>		// 获得所有任务名<br>		Set&lt;String&gt; jobNames = this.getJobNamesFromProp(jobProp);<br>		// 逐个加载任务<br>		for (String jobName : jobNames) {<br>			loadJob(jobProp, jobName);<br>		}<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> loadJob<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 加载一个任务<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> jobProp<br>	 *            job配置<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> jobName<br>	 *            job名<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	private void loadJob(Prop jobProp, String jobName) {<br>		// 任务开关，默认开启<br>		Boolean enable = jobProp.getBoolean(jobName + &quot;.enable&quot;, Boolean.TRUE);<br>		// 任务被禁用，直接返回<br>		if (!enable) {<br>			return;<br>		}<br>		// 创建要执行的任务<br>		Runnable task = createTask(jobName, jobProp.get(jobName + &quot;.class&quot;));<br>		// 任务类型<br>		String taskType = jobProp.get(jobName + &quot;.type&quot;);<br>		if (StrKit.isBlank(taskType)) {<br>			throw new RuntimeException(&quot;Please set &quot; + jobName + &quot;.type&quot;);<br>		}<br>		// 任务表达式<br>		String expr = jobProp.get(jobName + &quot;.expr&quot;);<br>		if (StrKit.isBlank(expr)) {<br>			throw new RuntimeException(&quot;Please set &quot; + jobName + &quot;.expr&quot;);<br>		}<br>		// 依据任务类型，开始调度任务<br>		scheduleJobByType(jobName, taskType, expr, task);<br>		LOG.info(&quot;--------load job: &quot; + jobName + &quot; successfully--------&quot;);<br>		LOG.info(&quot;class: &quot; + jobProp.get(jobName + &quot;.class&quot;));<br>		LOG.info(&quot;type : &quot; + taskType);<br>		LOG.info(&quot;expr : &quot; + expr);<br>		LOG.info(&quot;----------------&quot;);<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> scheduleJobByType<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 依据任务类型，开始调度任务<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> jobName<br>	 *            任务名<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> taskType<br>	 *            任务类型<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> expr<br>	 *            调度表达式<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> task<br>	 *            执行的任务<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	private void scheduleJobByType(String jobName, String taskType, String expr, Runnable task) {<br>		if (&quot;cron&quot;.equals(taskType)) {<br>			this.cronSchedule(task, expr);<br>		} else if (&quot;fixedRate&quot;.equals(taskType)) {<br>			int periodSeconds = 0;<br>			try {<br>				periodSeconds = Integer.parseInt(expr);<br>			} catch (NumberFormatException e) {<br>				throw new RuntimeException(jobName + &quot;.expr must be a number&quot;);<br>			}<br>			this.fixedRateSchedule(task, periodSeconds);<br>		} else if (&quot;fixedDelay&quot;.equals(taskType)) {<br>			int periodSeconds = 0;<br>			try {<br>				periodSeconds = Integer.parseInt(expr);<br>			} catch (NumberFormatException e) {<br>				throw new RuntimeException(jobName + &quot;.expr must be a number&quot;);<br>			}<br>			this.fixedDelaySchedule(task, periodSeconds);<br>		} else {<br>			throw new RuntimeException(&quot;Please set &quot; + jobName + &quot;.type to cron/fixedRate/fixedDelay&quot;);<br>		}<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> createTask<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 创建任务<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> jobName<br>	 *            任务名<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> taskClassName<br>	 *            任务类名<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@return</a> Runnable对象<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	private Runnable createTask(String jobName, String taskClassName) {<br>		if (taskClassName == null) {<br>			throw new RuntimeException(&quot;Please set &quot; + jobName + &quot;.className&quot;);<br>		}<br><br>		Object temp = null;<br>		try {<br>			temp = Class.forName(taskClassName).newInstance();<br>		} catch (Exception e) {<br>			throw new RuntimeException(&quot;Can not create instance of class: &quot; + taskClassName, e);<br>		}<br><br>		Runnable task = null;<br>		if (temp instanceof Runnable) {<br>			task = (Runnable) temp;<br>		} else {<br>			throw new RuntimeException(&quot;Can not create instance of class: &quot; + taskClassName<br>					+ &quot;. this class must implements Runnable interface&quot;);<br>		}<br>		return task;<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> getJobNamesFromProp<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 获得所有任务名<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@param</a> jobProp<br>	 *            job配置<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@return</a> 任务名集合<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	private Set&lt;String&gt; getJobNamesFromProp(Prop jobProp) {<br>		Map&lt;String, Boolean&gt; jobNames = new HashMap&lt;String, Boolean&gt;();<br>		for (Object item : jobProp.getProperties().keySet()) {<br>			String fullKeyName = item.toString();<br>			// 获得job名<br>			String jobName = fullKeyName.substring(0, fullKeyName.indexOf(&quot;.&quot;));<br>			jobNames.put(jobName, Boolean.TRUE);<br>		}<br>		return jobNames.keySet();<br>	}<br><br>	/**<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Title:</a> getBestPoolSize<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@Description:</a> 获得调度线程池大小<br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@return</a><br>	 * <a href=\"javascript:;\" class=\"qing-comment-aite\">@since</a> V1.0.0<br>	 */<br>	private static int getBestPoolSize() {<br>		try {<br>			final int cores = Runtime.getRuntime().availableProcessors();<br>			// 每个核有8个调度线程<br>			return cores * 8;<br>		} catch (Throwable e) {<br>			return 8;<br>		}<br>	}<br>}<br></pre>', '2017-02-20 16:51:50', 0, 0, '', '3ba1267bf2c245bf88c3fc66b2e09013', 0, 0, '3.gif', 1, 0);
